// App.js
import React, { useEffect, useReducer, useMemo } from 'react';
import debounce from 'lodash.debounce';
import { TransitionGroup, CSSTransition } from 'react-transition-group';
import './styles.css';

const PRIORITY_WEIGHTS = {
  high: 3,
  medium: 2,
  low: 1
};

const initialState = {
  tasks: [],
  filteredTasks: [],
  loading: true,
  error: null,
  priorityFilter: 'all'
};

function reducer(state, action) {
  switch (action.type) {
    case 'SET_TASKS':
      return {
        ...state,
        tasks: action.payload,
        filteredTasks: action.payload,
        loading: false
      };
    case 'SET_ERROR':
      return { ...state, error: action.payload, loading: false };
    case 'SET_FILTER':
      const filter = action.payload;
      return {
        ...state,
        priorityFilter: filter,
        filteredTasks:
          filter === 'all'
            ? state.tasks
            : state.tasks.filter(task => task.priority === filter)
      };
    default:
      return state;
  }
}

function calculateUrgencyScore(task) {
  const deadlineDate = new Date(task.deadline);
  const currentDate = new Date();
  const timeDiff = deadlineDate.getTime() - currentDate.getTime();
  const daysDiff = Math.ceil(timeDiff / (1000 * 3600 * 24));
  const adjustedDays = daysDiff < 0 ? daysDiff * 10 : Math.max(0.5, daysDiff);
  return adjustedDays / PRIORITY_WEIGHTS[task.priority];
}

function TaskManager() {
  const [state, dispatch] = useReducer(reducer, initialState);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        await new Promise(resolve => setTimeout(resolve, 1000));
        const mockTasks = [
          { id: 1, title: 'Complete project proposal', deadline: '2025-05-25T10:00:00', priority: 'high' },
          { id: 2, title: 'Review team submissions', deadline: '2025-05-21T15:30:00', priority: 'medium' },
          { id: 3, title: 'Update documentation', deadline: '2025-05-28T12:00:00', priority: 'low' },
          { id: 4, title: 'Prepare client presentation', deadline: '2025-05-19T09:00:00', priority: 'high' },
          { id: 5, title: 'Weekly team meeting', deadline: '2025-05-22T14:00:00', priority: 'medium' },
          { id: 6, title: 'Budget planning', deadline: '2025-05-30T16:00:00', priority: 'low' },
          { id: 7, title: 'Stakeholder report', deadline: '2025-05-18T11:00:00', priority: 'high' },
        ];
        const tasksWithUrgency = mockTasks.map(task => ({
          ...task,
          urgencyScore: calculateUrgencyScore(task)
        }));
        dispatch({ type: 'SET_TASKS', payload: tasksWithUrgency });
      } catch {
        dispatch({ type: 'SET_ERROR', payload: 'Failed to fetch tasks' });
      }
    };
    fetchTasks();
  }, []);

  const debouncedFilterChange = useMemo(
    () => debounce(value => dispatch({ type: 'SET_FILTER', payload: value }), 300),
    []
  );

  const handleFilterChange = e => debouncedFilterChange(e.target.value);

  const sortedTasks = [...state.filteredTasks].sort((a, b) => a.urgencyScore - b.urgencyScore);

  return (
    <div className="app-container">
      <div className="header">
        <h1>Task Manager Dashboard</h1>
        <select onChange={handleFilterChange} defaultValue="all">
          <option value="all">All</option>
          <option value="high">High</option>
          <option value="medium">Medium</option>
          <option value="low">Low</option>
        </select>
      </div>
      <div className="content">
        {state.loading ? (
          <p>Loading tasks...</p>
        ) : state.error ? (
          <p style={{ color: 'red' }}>{state.error}</p>
        ) : (
          <TransitionGroup className="task-list">
            {sortedTasks.map(task => (
              <CSSTransition key={task.id} timeout={300} classNames="task-item">
                <div className="task-item">
                  <h3>{task.title}</h3>
                  <p>Deadline: {new Date(task.deadline).toLocaleString()}</p>
                  <p>Priority: {task.priority}</p>
                  <p>Urgency: {getUrgencyLabel(task.urgencyScore)}</p>
                </div>
              </CSSTransition>
            ))}
          </TransitionGroup>
        )}
      </div>
    </div>
  );
}

function getUrgencyLabel(score) {
  if (score < 0) return 'Critical';
  if (score < 1) return 'Urgent';
  if (score < 3) return 'High';
  if (score < 7) return 'Medium';
  return 'Low';
}

export default TaskManager;
