import { useState, useEffect } from 'react';
import PriorityBadge from './components/PriorityBadge';
import UrgencyIndicator from './components/UrgencyIndicator';
import './styles.css';

const PRIORITY_WEIGHTS = {
  high: 3,
  medium: 2,
  low: 1
};

export default function TaskManager() {
  const [tasks, setTasks] = useState([]);
  const [filteredTasks, setFilteredTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [priorityFilter, setPriorityFilter] = useState('all');

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        setLoading(true);
        await new Promise(resolve => setTimeout(resolve, 1000));
        const mockTasks = [
          { id: 1, title: 'Complete project proposal', deadline: '2025-05-25T10:00:00', priority: 'high' },
          { id: 2, title: 'Review team submissions', deadline: '2025-05-21T15:30:00', priority: 'medium' },
          { id: 3, title: 'Update documentation', deadline: '2025-05-28T12:00:00', priority: 'low' },
          { id: 4, title: 'Prepare client presentation', deadline: '2025-05-19T09:00:00', priority: 'high' },
          { id: 5, title: 'Weekly team meeting', deadline: '2025-05-22T14:00:00', priority: 'medium' },
          { id: 6, title: 'Budget planning', deadline: '2025-05-30T16:00:00', priority: 'low' },
          { id: 7, title: 'Stakeholder report', deadline: '2025-05-18T11:00:00', priority: 'high' },
        ];
        const processed = mockTasks.map(task => ({
          ...task,
          urgencyScore: calculateUrgencyScore(task)
        }));
        setTasks(processed);
        setFilteredTasks(processed);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch tasks.');
        setLoading(false);
      }
    };

    fetchTasks();
  }, []);

  function calculateUrgencyScore(task) {
    const deadline = new Date(task.deadline);
    const now = new Date();
    const diffDays = Math.ceil((deadline - now) / (1000 * 3600 * 24));
    const adjusted = diffDays < 0 ? diffDays * 10 : Math.max(0.5, diffDays);
    return adjusted / PRIORITY_WEIGHTS[task.priority];
  }

  useEffect(() => {
    if (priorityFilter === 'all') {
      setFilteredTasks(tasks);
    } else {
      setFilteredTasks(tasks.filter(task => task.priority === priorityFilter));
    }
  }, [priorityFilter, tasks]);

  const handleFilterChange = (e) => {
    setPriorityFilter(e.target.value);
  };

  const isOverdue = (deadline) => new Date(deadline) < new Date();

  const formatDate = (str) => {
    return new Date(str).toLocaleString(undefined, {
      year: 'numeric', month: 'short', day: 'numeric',
      hour: '2-digit', minute: '2-digit'
    });
  };

  const sortedTasks = [...filteredTasks].sort((a, b) => a.urgencyScore - b.urgencyScore);

  if (loading) return <div className="loading">Loading tasks...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="container">
      <h1>Task Manager Dashboard</h1>

      <div className="filter">
        <label>Filter by Priority:</label>
        <select value={priorityFilter} onChange={handleFilterChange}>
          <option value="all">All Priorities</option>
          <option value="high">High</option>
          <option value="medium">Medium</option>
          <option value="low">Low</option>
        </select>
      </div>

      <div className="task-table">
        <div className="task-row header">
          <div>Task</div>
          <div>Deadline</div>
          <div>Priority</div>
          <div>Urgency</div>
        </div>

        {sortedTasks.length === 0 ? (
          <div className="no-tasks">No tasks found</div>
        ) : (
          sortedTasks.map(task => (
            <div key={task.id} className={`task-row ${isOverdue(task.deadline) ? 'overdue' : ''}`}>
              <div className="task-title">{task.title}</div>
              <div>{formatDate(task.deadline)}</div>
              <div><PriorityBadge priority={task.priority} /></div>
              <div><UrgencyIndicator score={task.urgencyScore} /></div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
